package merkle

import (
	"bytes"
	"testing"

	shell "github.com/ipfs/go-ipfs-api"
)

func TestComputeNodesCIDEqualsNode0(t *testing.T) {
	t.Skip("Requires running ipfs daemon")
	lvsJSON := `{"Leaves":[{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""},{"Data":""}]}`
	shares, err := parseSharesFromJSON(bytes.NewReader([]byte(lvsJSON)))
	input := make([][]byte, len(shares))
	for i, share := range shares {
		input[i] = share.Data
	}
	if err != nil {
		t.Fatal(err)
	}
	_, nodes := ComputeNodes(input)
	sh := shell.NewLocalShell()
	cid, err := sh.DagPut(lvsJSON, "json", "merkle-leaves")
	if err != nil {
		t.Fatal(err)
	}
	treeCidStr := nodes[0].Cid().String()
	if cid != treeCidStr {
		t.Fatal("Don't match")
	}
	t.Logf("cid: %v\n", cid)
}
